package Class4;

public class EvenOdd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Even Number");
		for(int i=0;i<100;i=i+2) {
			System.out.print(i+" ");
		}
		System.out.println();
		System.out.println("Odd number");
		for(int i=1;i<100;i=i+2) {
			System.out.print(i+" ");
		}
		
	}

}
