package Class4;

public class EvenOddWhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i=0;
		System.out.println("Even number");
		while(i<=100) {
			System.out.print(i+" ");
			i=i+2;
		}
		
		int j=1;
		System.out.println("Odd number");
		while(j<=100) {
			System.out.print(j+" ");
			j=j+2;
		}
	}

}
