package Day1;

public class OddEvenNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("Printing all the even number from 0 to 100");
		for(int i=0;i<=100;i=i+2) {
			System.out.print(i+" ");
		}
		
		System.out.println();
		System.out.println();
		System.out.println();
		
		System.out.println("Printing all the even number from 0 to 100");
		for(int i=1;i<=100;i=i+2) {
			System.out.print(i+" ");
		}
	}

}
